import React from 'react';
import {Link} from 'react-router-dom';
import { CSSGrid, layout } from 'react-stonecutter';
import images from '../api/Collections';
import Footer from "../component/footer/Footer";



const Kids = ({match, size}) => {

    return (
        <div>
            <div className="spacer-top container-fluid content">
                <CSSGrid
                    className="spacer-top"
                    component="ul"
                    columns={5}
                    columnWidth={150}
                    gutterWidth={5}
                    gutterHeight={5}
                    layout={layout.pinterest}
                    duration={800}
                    easing="ease-out"
                >
                    {images.filter((title) => title.title === 'kids').map(i => (
                        <figure
                            key={i.src}
                            className="image effect-sadie"
                        >
                            <Link to={{
                                pathname: `/viewpage/${i.id}`
                            }}> <img src={`${i.src}.jpg`} alt={i.label}/>
                                <figcaption>
                                    <h2>{i.label}</h2>
                                    <p>{i.title}</p>
                                </figcaption>
                            </Link>
                        </figure>
                    ))}
                </CSSGrid>
            </div>
            <Footer/>
        </div>
    )
};

export default Kids;
