import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';
import Masonry from 'react-masonry-css';
import images from '../api/Collections';
import Footer from "../component/footer/Footer";

const breakpointColumnsObj = {
    default: 3,
    992: 2,
    767: 1
};

class Home extends Component {
    render() {
        const firstTime = true;
        const delay = firstTime ? 500 : 0;
        return (
            <div>
                <div className="container-fluid content">
                    <Masonry
                        breakpointCols={breakpointColumnsObj}
                        className="my-masonry-grid spacer-top"
                        columnClassName="my-masonry-grid_column"
                    >
                        {images.map(i => (
                            <ReactCSSTransitionGroup transitionName="fade"
                                                     transitionAppear={true}
                                                     transitionAppearTimeout={500}
                                                     transitionEnterTimeout={500}
                                                     transitionLeaveTimeout={300}
                                                     key={i.src}>
                                <figure
                                    className="image effect-sadie"
                                    style={{ 'transition-delay': `${i * delay}ms` }}
                                >
                                    <Link to={{
                                        pathname: `/viewpage/${i.id}`
                                    }}> <img src={`${i.src}.jpg`} alt={i.label}/>
                                        <figcaption>
                                            <h2>{i.label}</h2>
                                            <p>{i.title}</p>
                                        </figcaption>
                                    </Link>
                                </figure>
                            </ReactCSSTransitionGroup>
                        ))}
                    </Masonry>
                </div>
                <Footer/>
            </div>
        );
    }
}


export default Home;
