import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import Masonry from 'react-masonry-css';
import images from '../api/Collections';
import Footer from "../component/footer/Footer";


const breakpointColumnsObj = {
    default: 3,
    992: 2,
    767: 1
};

class Home extends Component {
    constructor(props) {
        super(props);

        this.state = {
            didLoad: false
        }
    }

    onLoad = () => {
        this.setState({
            didLoad: true
        })
    }

    render() {
        const style = this.state.didLoad ? {filter: 'blur(10px)'} : {filter: 'blur(0px)'};
        return (
            <div>
                <div className="container-fluid content">
                    <Masonry
                        breakpointCols={breakpointColumnsObj}
                        className="my-masonry-grid spacer-top"
                        columnClassName="my-masonry-grid_column"
                    >
                        {images.map((i, index) => (

                                <figure
                                    key={index}
                                    className="image effect-sadie"
                                    style={{animationDelay: `${index * 100}ms`}}
                                >
                                    <Link to={{
                                        pathname: `/viewpage/${i.id}`
                                    }}> <img src={`${i.src}.jpg`} alt={i.label}
                                             style={style}
                                             onLoad={this.onLoad}
                                    />
                                        <figcaption>
                                            <h2>{i.label}</h2>
                                            <p>{i.title}</p>
                                        </figcaption>
                                    </Link>
                                </figure>

                            )
                        )}
                    </Masonry>
                </div>
                <Footer/>
            </div>
        );
    }
}

export default Home;
