import React, {Component} from 'react';
import {Link} from 'react-router-dom';
import {TransitionGroup, CSSTransition} from 'react-transition-group';
import Masonry from 'react-masonry-css';
import images from '../api/Collections';
import Footer from "../component/footer/Footer";

const breakpointColumnsObj = {
    default: 3,
    992: 2,
    767: 1
};

class Home extends Component {
    render() {
        let delay_index = -1;
        let delay_jump = 100;
        return (
            <div>
                <div className="container-fluid content">
                    <Masonry
                        breakpointCols={breakpointColumnsObj}
                        className="my-masonry-grid spacer-top"
                        columnClassName="my-masonry-grid_column"
                    >
                        <TransitionGroup>
                        {images.map((i, index) => {
                            delay_index += offer.isNew ? 1 : 0;
                            const delay = Math.max(0, delay_index * 100);
                            return(
                                    <CSSTransition

                                    >
                                        <figure
                                            key={i.src}
                                            className="image effect-sadie"

                                        >
                                            <Link to={{
                                                pathname: `/viewpage/${i.id}`
                                            }}> <img src={`${i.src}.jpg`} alt={i.label}/>
                                                <figcaption>
                                                    <h2>{i.label}</h2>
                                                    <p>{i.title}</p>
                                                </figcaption>
                                            </Link>
                                        </figure>
                                    </CSSTransition>
                                )

                        })}
                        </TransitionGroup>
                    </Masonry>
                </div>
                <Footer/>
            </div>
        );
    }
}


export default Home;
