import React from 'react';
import {Link} from 'react-router-dom';
import Masonry from 'react-masonry-css';
import images from '../api/Collections';
import Footer from "../component/footer/Footer";
const breakpointColumnsObj = {
    default: 3,
    1100: 3,
    700: 2,
    500: 1
};

const Kids = ({match, size}) => {

    return (
        <div>
            <div className="spacer-top container-fluid content">
                <Masonry
                    className="spacer-top my-masonry-grid"
                    columnClassName="my-masonry-grid_column"
                >
                    {images.filter((title) => title.title === 'kids').map(i => (
                        <figure
                            key={i.src}
                            className="image effect-sadie"
                        >
                            <Link to={{
                                pathname: `/viewpage/${i.id}`
                            }}> <img src={`${i.src}.jpg`} alt={i.label}/>
                                <figcaption>
                                    <h2>{i.label}</h2>
                                    <p>{i.title}</p>
                                </figcaption>
                            </Link>
                        </figure>
                    ))}
                </Masonry>
            </div>
            <Footer/>
        </div>
    )
};

export default withSize()(Kids);
