import React, {Component} from 'react';
import {Link} from 'react-router-dom';

import StackGrid, {transitions, easings} from 'react-stack-grid';
import images from '../api/Collections';
import Footer from "../component/footer/Footer";

const transition = transitions.helix;

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = { width: '33.33%'};
        this.updatePredicate = this.updatePredicate.bind(this);
    }
    componentDidMount() {
        this.updatePredicate();
        window.addEventListener("resize", this.updatePredicate);

    }
    componentWillUnmount() {
        this.updatePredicate();
        window.removeEventListener("resize", this.updatePredicate);

    }
    updatePredicate() {
        if(window.innerWidth < 800 ){
        this.setState({ width: '100%' });
            this.forceUpdate();
        } else{
            this.setState({ width: '33.33%' });
            }

    }

    render() {
        const width = this.state.width;
        console.log(this.state.width, window.innerWidth);

        return (
            <div>
                <div className="container-fluid content">
                    <StackGrid
                        className="spacer-top"
                        monitorImagesLoaded={true}

                        columnWidth={width}
                        duration={600}
                        gutterWidth={10}
                        gutterHeight={5}
                        easing={easings.cubicOut}
                        appearDelay={60}
                        appear={transition.appear}
                        appeared={transition.appeared}
                        enter={transition.enter}
                        entered={transition.entered}
                        leaved={transition.leaved}
                    >
                        {images.map(i => (
                            <figure
                                key={i.src}
                                className="image effect-sadie"
                            >
                                <Link to={{
                                    pathname: `/viewpage/${i.id}`
                                }}> <img src={`${i.src}.jpg`} alt={i.label}/>
                                    <figcaption>
                                        <h2>{i.label}</h2>
                                        <p>{i.title}</p>
                                    </figcaption>
                                </Link>
                            </figure>
                        ))}
                    </StackGrid>
                </div>
                <Footer/>
            </div>
        );
    }
}


export default Home;
